package ch02.sec01;

public class VariableInitializationExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 변수 value 선언
		int value = 10;
		
		//연산결과를 변수 result의 초기값으로 설정
		int result = value+10;
		
		//변수 result 값을 읽고 콘솔에 입력
		System.out.println(result);
	}

}
